using namespace std;

class Solution_DP {
public:
	int flag[500][500];

	bool isMatch(const char *s, const char *p) {
		memset(flag,-1,sizeof(flag));
		return dfs(s,p);
	}

    bool dfs(const char *s, const char *p) {
		if(flag[strlen(s)][strlen(p)] != -1) return (bool)flag[strlen(s)][strlen(p)];
        if(*s==0 && *p==0) 
		{
			flag[strlen(s)][strlen(p)]=1;
			return true;
		}
		if(*s!=0 && *p==0) 
		{
			flag[strlen(s)][strlen(p)]=0;
			return false;
		}
		if(*s==0)
		{
			const char* tmp= p;
			while(*tmp != 0)
			{
				if(*tmp != '*') 
				{
					flag[strlen(s)][strlen(p)]=0;
					return false;
				}
				tmp++;
			}
			flag[strlen(s)][strlen(p)]=1;
			return true;
		}
		if(*s==*p || *p=='?') 
		{
			flag[strlen(s)][strlen(p)]=dfs(s+1,p+1);
			return flag[strlen(s)][strlen(p)];
		}
		if(*p=='*') 
		{
			flag[strlen(s)][strlen(p)] = dfs(s+1,p) || dfs(s+1,p+1);
			return flag[strlen(s)][strlen(p)];
		}
		flag[strlen(s)][strlen(p)]=0;
		return false;
    }
};

class Solution_old {
public:
    bool isMatch(const char *s, const char *p) {
        const char * pos_s=s;
		const char * pos_p=NULL;
		while(*s!=0)
		{
			if(*s==*p || *p=='?') {s++;p++;}
			else
			if(*p=='*')
			{
				pos_p=p;
				pos_s=s;
				p++;
			}
			else
			if(*s!=*p)
			{
				if(pos_p==NULL) return false;
				pos_s++;
				s=pos_s;
				p=pos_p+1;
			}
		}
		if(*p!=0)
			while(*p!=0)
			{
				if(*p!='*') return false;
				p++;
			}
		return true;
    }
};

//2nd pass: 2015-02-22
class Solution_2nd {
public:
    bool isMatch(const char *s, const char *p) {
        const char *s_pos,*p_pos=NULL;
        while(*s)
        {
            if(*p=='*') {s_pos=s; p_pos=p; p++; continue;}
            if(*p==*s||*p=='?') {p++;s++; continue;}
            if(p_pos) {s=++s_pos; p=p_pos+1;}
            else return false;
        }
        while(*p=='*')p++;
        return (*p=='\0');
    }
};

//3rd pass: 2015-03-28
/*
while loop *s
1. *s == *p || *p == '?', ++s, ++p
2. *p == '*', record s_pos and p_pos, ++p
3. else means miss match
    3.1 no p_pos, just false
    3.2 s=++s_pos, p=p_pos+1
done with while loop, *p must be '*' until end.
*/
class Solution {
public:
    bool isMatch(const char *s, const char *p) {
        const char* s_pos=0, *p_pos=0;
        while(*s){
            if(*s==*p || *p=='?') {++s; ++p; continue;}
            if(*p=='*') {s_pos=s; p_pos=p; ++p; continue;}
            if(p_pos==0) return false; else {s=++s_pos; p=p_pos+1;}
        }
        while(*p) if(*p!='*') return false; else ++p;
        return true;
    }
};

int main()
{
	Solution *s = new Solution();
	
	cout<<s->isMatch("","*")<<endl;//1
	cout<<s->isMatch("aa","a")<<endl;//0
	cout<<s->isMatch("aa","aa")<<endl;//1
	cout<<s->isMatch("aaa","aa")<<endl;//0
	cout<<s->isMatch("aa","*")<<endl;//1
	cout<<s->isMatch("aa","a*")<<endl;//1
	cout<<s->isMatch("ab","?*")<<endl;//1
	cout<<s->isMatch("aab","c*a*b")<<endl;//0
	cout<<s->isMatch("aaaabaaaabbbbaabbbaabbaababbabbaaaababaaabbbbbbaabbbabababbaaabaabaaaaaabbaabbbbaababbababaabbbaababbbba","*****b*aba***babaa*bbaba***a*aaba*b*aa**a*b**ba***a*a*")<<endl;//1
	cout<<s->isMatch("bbbbbaabaaaaaaababb","bbaabba")<<endl;//0
	//cout<<s->isMatch("", "**")<<endl;

	system("pause");
	return 0;
}