using namespace std;

class Solution_DP {
public:
	int flag[500][500];

	bool isMatch(const char *s, const char *p) {
		memset(flag,-1,sizeof(flag));
		return dfs(s,p);
	}

    bool dfs(const char *s, const char *p) {
		if(flag[strlen(s)][strlen(p)] != -1) return (bool)flag[strlen(s)][strlen(p)];
        if(*s==0 && *p==0) 
		{
			flag[strlen(s)][strlen(p)]=1;
			return true;
		}
		if(*s!=0 && *p==0) 
		{
			flag[strlen(s)][strlen(p)]=0;
			return false;
		}
		if(*s==0)
		{
			const char* tmp= p;
			while(*tmp != 0)
			{
				if(*tmp != '*') 
				{
					flag[strlen(s)][strlen(p)]=0;
					return false;
				}
				tmp++;
			}
			flag[strlen(s)][strlen(p)]=1;
			return true;
		}
		if(*s==*p || *p=='?') 
		{
			flag[strlen(s)][strlen(p)]=dfs(s+1,p+1);
			return flag[strlen(s)][strlen(p)];
		}
		if(*p=='*') 
		{
			flag[strlen(s)][strlen(p)] = dfs(s+1,p) || dfs(s+1,p+1);
			return flag[strlen(s)][strlen(p)];
		}
		flag[strlen(s)][strlen(p)]=0;
		return false;
    }
};

class Solution {
public:
    bool isMatch(const char *s, const char *p) {
        const char * pos_s=s;
		const char * pos_p=NULL;
		while(*s!=0)
		{
			if(*s==*p || *p=='?') {s++;p++;}
			else
			if(*p=='*')
			{
				pos_p=p;
				pos_s=s;
				p++;
			}
			else
			if(*s!=*p)
			{
				if(pos_p==NULL) return false;
				pos_s++;
				s=pos_s;
				p=pos_p+1;
			}
		}
		if(*p!=0)
			while(*p!=0)
			{
				if(*p!='*') return false;
				p++;
			}
		return true;
    }
};

int main()
{
	Solution *s = new Solution();
	
	cout<<s->isMatch("","*")<<endl;
	cout<<s->isMatch("aa","a")<<endl;
	cout<<s->isMatch("aa","aa")<<endl;
	cout<<s->isMatch("aaa","aa")<<endl;
	cout<<s->isMatch("aa","*")<<endl;
	cout<<s->isMatch("aa","a*")<<endl;
	cout<<s->isMatch("ab","?*")<<endl;
	cout<<s->isMatch("aab","c*a*b")<<endl;
	cout<<s->isMatch("aaaabaaaabbbbaabbbaabbaababbabbaaaababaaabbbbbbaabbbabababbaaabaabaaaaaabbaabbbbaababbababaabbbaababbbba","*****b*aba***babaa*bbaba***a*aaba*b*aa**a*b**ba***a*a*")<<endl;
	cout<<s->isMatch("bbbbbaabaaaaaaababb","bbaabba")<<endl;
	//cout<<s->isMatch("", "**")<<endl;

	system("pause");
	return 0;
}